mapper.sql:
FROM HOUSE a JOIN BUILDING_UNIT c ON a.BUILDING_UNIT_ID = c.ID JOIN BUILDING d ON c.BUILDING_ID = d.ID JOIN REGION e ON e.ID = d.TOWN_ID JOIN REGION f ON f.ID = e.PARENT_ID WHERE 1=1
AND f.ID = #{param.districtId}
AND e.ID = #{param.townId}
AND d.BUILDING_NAME like '%${param.buildingName}%'
AND a.BUILDING_UNIT_ID = #{param.buildingUnitId}

  FROM TRADE a
JOIN HOUSE b
  ON a.HOUSE_ID = b.ID
JOIN BUILDING_UNIT c
  ON b.BUILDING_UNIT_ID = c.ID
JOIN BUILDING d
  ON c.BUILDING_ID = d.ID
JOIN REGION e
  ON e.ID = d.TOWN_ID
JOIN REGION f
  ON f.ID = e.PARENT_ID
LEFT JOIN IMAGE g
  ON g.ID = a.IMAGE_ID
  WHERE 1=1

    AND f.ID = #{param.districtId}


    AND e.ID = #{param.townId}


    AND a.PRICE >= #{param.priceBegin}


    AND a.PRICE <= #{param.priceEnd}


    AND b.ROOM > #{param.room}


    AND b.ROOM = #{param.room}


    AND b.SALOON = #{param.saloon}


    AND b.TOILET = #{param.toilet}


    AND b.AREA >= #{param.areaBegin}


    AND b.AREA <= #{param.areaEnd}


    AND d.SUBWAY = #{param.subway}


    AND d.BUILDING_NAME like '%${param.buildingName}%'


    AND to_char(a.RELEASE_TIME, 'YYYY-MM-DD') >= #{param.releaseDateBegin}


    AND to_char(a.RELEASE_TIME, 'YYYY-MM-DD') <= #{param.releaseDateEnd}

mapper.select:
SELECT a.ID TRADE_ID, g.TITLE IMG_TITLE, g.URL, a.TITLE, a.TYPE, e.NAME TOWN_NAME, d.BUILDING_NAME, d.BUILDING_ADDRESS, d.BUILDING_YEAR, c.FLOOR BUILDING_FLOOR, a.PRICE, b.AREA, b.ROOM, b.SALOON, b.TOILET, b.FLOOR, a.RELEASE_TIME
order by
a.PRICE ${param.sort}
b.AREA ${param.sort}
a.RELEASE_TIME ${param.sort}
a.RELEASE_TIME desc
limit #{start, jdbcType=INTEGER}, #{end, jdbcType=INTEGER}

mapper.update:
		update HOUSE

UPDATE_TIME = #{param.updateTime, jdbcType=TIMESTAMP},

		where id = #{param.id, jdbcType=VARCHAR}
