mapper.sql:
FROM TRADE a JOIN HOUSE b ON a.HOUSE_ID = b.ID JOIN BUILDING_UNIT c ON b.BUILDING_UNIT_ID = c.ID JOIN BUILDING d ON c.BUILDING_ID = d.ID JOIN REGION e ON e.ID = d.TOWN_ID LEFT JOIN IMAGE f ON f.ID = a.IMAGE_ID WHERE 1=1
AND b.ROOM > #{param.pattern}
AND b.ROOM = #{param.pattern}
AND b.AREA >= #{param.areaBegin} AND b.AREA <= #{param.areaEnd}

mapper.update:
		update TRADE

UPDATE_TIME = #{param.updateTime, jdbcType=TIMESTAMP},

		where id = #{param.id, jdbcType=VARCHAR}
